import React, { useState } from 'react';
import './App.css';

const demoSong = {
  name: 'Love Trip',
  artist: 'Ulchero, Couple N',
  cover: 'https://p2.music.126.net/2Qn6Qn6Qn6Qn6Qn6Qn6Qn6Q==/109951165888888888.jpg',
};

function App() {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isStopped, setIsStopped] = useState(true);

  const handlePlay = () => {
    setIsPlaying(true);
    setIsStopped(false);
