  const handlePause = () => {
    setIsPlaying(false);
    // TODO: ÂØπÊé•ÂêéÁ´ØAPI
  };
  const handleStop = () => {
    setIsPlaying(false);
    setIsStopped(true);
    // TODO: ÂØπÊé•ÂêéÁ´ØAPI
  };

  return (
    <div className="player-bg">
      <div className="player-container">
        <div className="cover-section">
          <img className="cover-img" src={demoSong.cover} alt="cover" />
        </div>
        <div className="info-section">
          <div className="song-title">{demoSong.name}</div>
          <div className="song-artist">{demoSong.artist}</div>
