            <button className="ctrl-btn main" onClick={handlePlay}>
              <span role="img" aria-label="play">‚ñ∂Ô∏è</span>
            </button>
          )}
          <button className="ctrl-btn" disabled>
            <span role="img" aria-label="next">‚è≠Ô∏è</span>
          </button>
          <button className="ctrl-btn" onClick={handleStop}>
            <span role="img" aria-label="stop">‚èπÔ∏è</span>
          </button>
        </div>
        <div className="progress-section">
          <div className="progress-bar-bg">
            <div className="progress-bar" style={{ width: isPlaying ? '60%' : isStopped ? '0%' : '30%' }}></div>
          </div>
        </div>
